E2Helper.Descriptions["streamStart(e:ns)"] = "Streams a .mp3 sound from an URL at E's position.\nThis function has an antispam, see the streamCanStart function.\nThe default volume is set to 1."
E2Helper.Descriptions["streamStart(e:nns)"] = "Streams a .mp3 sound from an URL at E's position.\nThis function has an antispam, see the streamCanStart function."
E2Helper.Descriptions["streamStart(e:nsn)"] = "Streams a .mp3 sound from an URL at E's position.\nThis function has an antispam, see the streamCanStart function."
E2Helper.Descriptions["streamStop(n)"] = "Stops the Nth stream started earlier using the streamStart function."
E2Helper.Descriptions["streamVolume(nn)"] = "Changes the Nth stream's volume.\nVolume is between 0 and 1."
E2Helper.Descriptions["streamRadius(nn)"] = "ONLY WORKS WITH 3D-DISABLED STREAMS!\nChanges the Nth stream's radius.\nRadius needs to be greater than 0.\nSee the streamMaxRadius function."
E2Helper.Descriptions["streamHelp()"] = "Opens the StreamCore's workshop page which contains the full documentation using the Steam's Overlay and returns its URL."
E2Helper.Descriptions["streamCanStart()"] = "Returns 1 if you are able to start a new stream."
E2Helper.Descriptions["streamLimit()"] = "Returns the value of the streamcore_antispam_seconds server cvar."
E2Helper.Descriptions["streamMaxRadius()"] = "Returns the value of the streamcore_maxradius server cvar."
E2Helper.Descriptions["streamAdminOnly()"] = "Returns 1 if the StreamCore is admin-only.\nSee the streamcore_adminonly server cvar."
E2Helper.Descriptions["streamDisable3D(n)"] = "If N > 0, the streams will use a \"fake\" 3D mode.\nThis is a workaround to obtain louder sounds.\nActive streams will not be changed."